import t from 'tcomb';
import applyPatch from 'immpatch';

import JSONPointer from './JSONPointer';
import { get, unbox, box } from './state';

const emptyInstance = {
  type:  t.Any,
  state: box({})
};

/**
 * Context is passed to all components.
 */
class Context {

  /**
   * Context constructors
   *
   * @param {Object} instance - instances sdds
   * @param {Object} instance.state - states  sd
   * @param {Transform} instance.transform - transform sdds
   */
  constructor(instance = {}) {
    Object.assign(this, emptyInstance, instance);

    Object.freeze(this);
  }


  /**
   * create Context of state, transform is identity
   * @param  {object} state - state of ctx
   * @return {Context}
   */

  static ofState(state = {}) {
    return new Context({
      state: box(state)
    });
  }

  /**
   * get value from state
   * @param {string|JSONPointer} path = '' - pointer to value
   * @param {boolean} toJS = false - unbox value?
   * @return {any} value
   */
  get(path = '', toJS = false) {
    if(t.Boolean.is(path)) return this.get('', path);

    const result = get(this.state, JSONPointer.ofString(path));

    return toJS ? unbox(result) : result;
  }

  /**
   * check if ctx has  changed
   * @param  {Context} last - the last COntext
   * @return {boolean}
   */
  changed(last) {
    const keys = last.state.keySeq().toJS();

    return keys.some(key => this.get(key) !== last.get(key));
  }

  update(patchSet) {
    if(patchSet.length === 0) return this;

    const newState = applyPatch(this.state, patchSet);
    // assert type

    const rawState = newState.toJS();

    this.type(rawState);

    return new Context({
      type:  this.type,
      // transform: this.transform,
      state: newState
    });
  }


}

export default Context;

import t from 'tcomb';

/**
 * An array of Strings
 * TokenList holds JSONPath tokens.
 * @typedef {Array<string>} TokenList
 */
const TokenList = t.list(t.String);

/**
 * 	JSON Pointer defines a string format for identifying a specific value within a JSON document.
 *  This class holds methods for manipulation of pointers.
 *
 *  For more information:
 *   - http://tools.ietf.org/html/rfc6901
 *   - http://jsonpatch.com/#json-pointer
 */
class JSONPointer {

  /**
   * JSONPointer constructors
   *
   * @param {Object} opts - options.
   * @param {TokenList} opts.tokens - json path tokens
   */
  constructor(opts = {}) {
    const tokens = TokenList(opts.tokens || []);

    this.tokens = tokens;

    Object.freeze(this.tokens);
    Object.freeze(this);
  }

  static ofTokens(tokens) {
    return new JSONPointer({ tokens });
  }

  /**
   * parse str and return JSONPointer
   *
   * If x starts with '/' create will treat x as a JSONPointer.
   * All other string will be treated like immutable js pointer (eg.: 'foo.bar')
   *
   * @param {string} str - pointer string
   * @returns {JSONPointer}
   */
  static ofString(str) {
    if(!t.String.is(str))
      throw new Error('JSONPointer::ofString - expected string');

    if(str === '') return new JSONPointer({
      tokens: []
    });

    // pointer to key "" (rfc)
    if(str === '/') return new JSONPointer({
      tokens: ['']
    });

    if(str.startsWith('/')) return new JSONPointer({
      tokens: str.split('/').slice(1)
    });

    return new JSONPointer({
      tokens: str.split('.')
    });
  }

  /**
   * get rfc string representation
   *
   * @returns {string}
   */
  toRFC() {
    return '/'.concat(this.tokens.join('/'));
  }

  /**
   * get immutable js string representation
   *
   * @returns {string}
   */
  toImmutable() {
    return this.tokens.join('.');
  }

  /**
   * get first token
   *
   * Return value is the first entry from token list.
   * @returns {string}
   */
  first() {
    return this.tokens[ 0 ] || null;
  }

  /**
   * get last token
   *
   * Return value is the last entry from token list.
   * @returns {string}
   */
  last() {
    return this.tokens[ this.tokens.length - 1 ] || null;
  }

  /**
   * return length of token array
   * @returns {number}
   */
  size() {
    return this.tokens.length;
  }

  /**
   * concat two JSONPointer
   *
   * @param {JSONPointer} sub - the pointer to concat
   * @returns {JSONPointer}
   */
  concat(sub) {
    if(!sub instanceof JSONPointer)
      throw new Error('JSONPointer#concat argument should be JSONPointer');

    return new JSONPointer({
      tokens: this.tokens.concat(sub.tokens)
    });
  }

  /**
   * slice JSONPointer
   *
   * slice has the same signature like Array.prototype.slice.
   *
   * @returns {JSONPointer}
   */
  slice() {
    return new JSONPointer({
      tokens: Array.prototype.slice.apply(this.tokens, arguments)
    });
  }
}

export default JSONPointer;

import t from 'tcomb';

const specificString = str => t.irreducible(`String: ${str}`, x => str === x);

const BasePatch = t.struct({
  path: t.String
});

const AddPatch = BasePatch.extend({
  op:    specificString('add'),
  value: t.Any
}, 'AddPatch');

const RemovePatch = BasePatch.extend({
  op: specificString('remove')
}, 'RemovePatch');

const ReplacePatch = BasePatch.extend({
  op:    specificString('replace'),
  value: t.Any
}, 'ReplacePatch');

/**
 * json patch type.
 * - only add, remove and replace patches are supported
 * - http://jsonpatch.com
 * @typedef {object} Patch
 */

export const Patch = t.union([AddPatch, RemovePatch, ReplacePatch], 'Patch');

import t from 'tcomb';
import mapObj from '@f/map-obj';

import JSONPointer from './JSONPointer';

function substitutePointer(pointer, targets) {
  const root = pointer.first();

  if(t.Nil.is(targets[ root ]))
    throw new Error(`No target found for key ${root}. Possible targets: ${targets.toString()}`);

  const tokens = targets[ root ].tokens.concat(pointer.tokens.slice(1));

  return JSONPointer.ofTokens(tokens);
}

/**
 * Map holds transform informations.
 *
 * @typedef {Map<string, JSONPointer>} Map
 */

const Map = t.dict(t.String, JSONPointer);

const parseTargets = desc => mapObj(JSONPointer.ofString, desc);


/**
 * Transform tracks changes to state structure,
 * is used for mounting transforms.
 */

class Transform {

  /**
   * Transform constructors
   *
   * @param {Map} map - instances sdds
   */

  constructor(map = Map({})) {
    this.length = Object.keys(map).length;
    this.map = map;

    Object.freeze(this.length);
    Object.freeze(this.map);
    Object.freeze(this);
  }

  /**
   * parse target keys to json pointer and return transform
   * @param {Object<string, string>} targets
   * @returns {Transform}
   */

  static ofTargets(targets = {}) {
    const map = parseTargets(targets);

    return new Transform(map);
  }

  /**
   * resolve keys from child transform with targets from object
   * @param {Transform} child - the child
   * @returns {Transform}
   */

  sub(child) {
    if(this.isIdentity()) return child;
    if(child.isIdentity()) return this;

    const childMap = mapObj(target => substitutePointer(target, this.map), child.map);

    return new Transform(childMap);
  }

  /**
   * apply transform on JSONPointer
   * @param {JSONPointer} pointer - pointer
   * @returns {Transform}
   */

  apply(pointer) {
    const target = pointer.first();

    if(t.Nil.is(this.map[ target ])) return pointer;

    const tail = pointer.slice(1);

    return this.map[ target ].concat(tail);
  }

  /**
   * returns targets (target pointer as rfc string)
   * @returns {Object<string, string>}
   */
  toTargets() {
    return mapObj(pointer => pointer.toRFC(), this.map);
  }

  /**
   * check if transforms are equal
   * @param {Transform} x - transfrom to check against
   * @returns {boolean}
   */
  equals(x) {
    if(this.length !== x.length) return false;

    return Object
      .keys(this.map)
      .every(target => x.map[ target ] && this.map[ target ].toRFC() === x.map[ target ].toRFC());
  }

  /**
   * check if transform is identify
   * @returns {boolean}
   */
  isIdentity() {
    return this.length === 0;
  }


}

export default Transform;

import { specDecorator } from './utils';
import check from './../utils/checkType';

/**
 * assert context type
 *
 * ```javascript
 * import t from 'tcomb'
 *
 * checkContextType(t.struct({
 * 	someProp: t.String
 * }))(model => { ... })
 * ```
 *
 * @param  {Type} T - type to check context again (tcomb type)
 * @return {HOC}
 */
export default function checkContextType(T) {
  const resultCache = new WeakMap();
  const spec = {
    deep:  false,
    model: function(component, model) {
      const cacheEntry = resultCache.get(model.context);

      if(cacheEntry === true) return model;
      if(cacheEntry === false) T(model.context.get(true));

      const unboxedCtx = model.context.get(true);
      const result = check(T, unboxedCtx);

      resultCache.set(model.context, result);
      if(result === false) T(unboxedCtx);

      return model;
    }
  };
  const decorator = specDecorator(spec);

  return comp => {
    return decorator(comp);
  };
}

import { normalize } from './utils/';

/**
 * apply multiple decorators on component
 *
 * ```javascript
 * component(
 * 	someDeoraotor(),
 * 	someOther()
 * )(model => { ... })
 * ```
 *
 * @param  {...HOC} decoration - list of component decorators (hocs)
 * @return {HOC}
 */

export default function component(...decoration) {
  return rawComponent => {
    const comp = normalize(rawComponent);

    return decoration
     .slice()
     .reverse()
     .reduce((target, decorator) => decorator(target), comp);
  };
}

import t from 'tcomb';

import { specDecorator, mapAttributes, isHandler } from './utils';


function createModel(model, controller) {
  function dispatch(name, payload) {
    if(!t.String.is(name)) return model.dispatch(name, payload);
    const target = controller[ name ];

    if(t.Nil.is(target)) throw new TypeError(`controller[${name}] is ${target}`);
    if(!t.Function.is(target)) return model.dispatch(target);
    const targetModel = Object.assign({}, model, { event: payload });
    const output = target(targetModel);

    return model.dispatch(output);
  }

  return Object.assign({}, model, { dispatch });
}

function createRender(component, model) {
  const output = component.render(model);

  return mapAttributes(output, function(prop, name) {
    if(!t.String.is(prop) || !isHandler(name)) return prop;
    return event => model.dispatch(prop, event);
  }, true);
}

/**
 * controller decorator.
 *
 * ```javascript
 * controller({
 * 	click: (model) => { ... }
 * })(({ dispatch }) => (
 * 	<input type='button' onClick={event => dispatch('click', event)} />
 * 	// or
 * 	<input type='button' onClick='click' />
 * ))
 * ```
 *
 * You can dispatch actions to handlers in your controller when you call
 * model.dispatch with the property name as first argument and an optional event as second argument.
 *
 * If the property is a function, then the function will be called with model as the only argument.
 * The model object has an additional property 'event', which contains the second argument from dispatch.
 * Otherwise the property will just be dispatched.
 *
 * @param  {Map<string, any>} controller - action map
 * @return {HOC}
 */
export default function(controller) {
  const spec = {
    deep:   false,
    cache:  new WeakMap(),
    model:  (component, model) => createModel(model, controller),
    render: (component, model) => createRender(component, model)
  };

  return specDecorator(spec);
}

