import mount from './mount';
import controller from './controller';
import memoize from './memoize';
import styler from './styler';
import checkContextType from './checkContextType';
import component from './component';
import name from './name';
import dispatchChangeSets from './dispatchChangeSets';
import logLifecycle from './log';

/**
 * Higher Order Component
 * @typedef {function(component: Component): Component} HOC
 */

export default {
  name,
  component,
  mount,
  controller,
  memoize,
  styler,
  checkContextType,
  dispatchChangeSets,
  logLifecycle
};

import pick from '@f/pick';

import { specDecorator } from './utils';

const nameCache = new WeakMap();

function componentName(component) {
  if(component.name) return component.name;
  const cacheEntry = nameCache.get(component);

  if(cacheEntry) return cacheEntry;
  const body =  Object.keys(component)
    .map(key => [key, `${component[ key ]}`])
    .map(([key, value]) => `  ${key}: ${value.split('\n')[ 0 ]}...`)
    .join('\n');
  const name =  `Component#{\n${body}\n}`;

  nameCache.set(component, name);
  return name;
}

const createHandler = name => (component, model) => {
  const handler = component[ name.slice(2).toLowerCase() ];

  return () => handler ? handler(model) : null;
};

const targetHandler = {
  render: function(component, model) {
    console.log('render', componentName(component));
    return component.render(model);
  },
  onUpdate: createHandler('onUpdate'),
  onCreate: createHandler('onCreate'),
  onRemove: createHandler('onRemove')
};

const defaultTargets = ['render', 'onUpdate', 'onCreate', 'onRemove'];

/**
 * log component lifecycle
 *
 * @return {HOC}
 */

export default function log(targets = defaultTargets) {
  const opts = {
    deep:  false,
    name:  'log',
    cache: new WeakMap()
  };
  const spec = Object.assign({}, pick(targets, targetHandler), opts);

  return specDecorator(spec);
}

import dekuMemoize from 'deku-memoize';

import { normalize } from './utils';

function defaultShouldUpdate(prev, next) {
  return next.context.changed(prev.context);
}

/**
 * memoize component
 *
 * @param  {function(nextModel: Model, prevModel: Model): boolean} shouldUpdate - list of component decorators (hocs)
 * @return {HOC}
 */

export default function(shouldUpdate = defaultShouldUpdate) {
  return rawComponent => {
    const component = normalize(rawComponent);
    const decoratedComponent = Object.assign({}, component, { shouldUpdate });

    return dekuMemoize(decoratedComponent);
  };
}

import { specDecorator } from './utils';
import Transform from './../Transform';
import JSONPointer from './../JSONPointer';
import Immutable from 'immutable';
import { get } from './../state';
import Context from './../Context';

const parsePath = patch => Object.assign({}, patch, { path: JSONPointer.ofString(patch.path) });
const transformPath = transform => patch => Object.assign({}, patch, { path: transform.apply(patch.path) });
const stringifyPath = patch => Object.assign({}, patch, { path: patch.path.toRFC() });

const createDispatch = (transform, dispatch) => rawPatchSet => {
  const patchSet = rawPatchSet
    .map(parsePath)
    .map(transformPath(transform))
    .map(stringifyPath);

  if(patchSet.length === 0) return;
  dispatch(patchSet);
};

function applyTransform(transform, ctx) {
  const state = Immutable.Map(transform.map)
    .map(pointer => get(ctx.state, pointer));

  return new Context({ state });
}

/**
 * transform ctx of component
 *
 * ```javascript
 * mount({
 * 	foo: '/some/value'
 * 	bar: '/here/is/another/val'
 * })(component)
 * ```
 *
 * @param  {Map<string, string>} targets - transform description
 * @return {HOC}
 */
export default function(targets) {
  const transform = Transform.ofTargets(targets);

  const spec = {
    deep:  true,
    cache: new WeakMap(),
    name:  'mount',
    model: function(component, model) {
      const context = applyTransform(transform, model.context);
      const dispatch = createDispatch(transform, model.dispatch);

      return Object.assign({}, model, { context, dispatch });
    }
  };

  return specDecorator(spec);
}

import { normalize } from './utils';

/**
 * add name to component - for debug messages
 *
 * @param  {string} name - component name
 * @return {HOC}
 */
export default name => function(rawComponent) {
  const component = normalize(rawComponent);

  return Object.assign({}, component, { name });
};

import inflection from 'inflection';
import { vnode as element } from 'deku';

import { specDecorator } from './utils';

const { isText, isEmpty }  = element;
const toDash = x => inflection.underscore(x).split('_').join('-');

function decorateChildren(vnode) {
  if(isText(vnode) || isEmpty(vnode) || !vnode.children) return vnode;
  const children = vnode.children.map(child => decorateVnode(child));

  return Object.assign({}, vnode, { children });
}

function decorateVnode(vnode) {
  if(isText(vnode) || isEmpty(vnode)) return vnode;

  const decoratedNode = decorateChildren(vnode);

  if(!vnode.attributes || !vnode.attributes.style)
    return decoratedNode;

  const rule = Object.keys(vnode.attributes.style)
    .map(key => `${toDash(key)}:${vnode.attributes.style[ key ]};`)
    .join('');

  const attributes = Object.assign({}, decoratedNode.attributes, { style: rule });

  return Object.assign({}, decoratedNode, { attributes });
}

const spec = {
  deep:   false,
  cache:  null,
  name:   'styler',
  render: function(component, model) {
    const vnode = component.render(model);

    return decorateVnode(vnode);
  }
};

const decorator = specDecorator(spec);

/**
 * add css styles as Object
 *
 * ```javascript
 * styler()(() => (<div style={{ color: 'green'}}>))
 * ```
 *
 * @return {HOC}
 */

export default function styler() {
  return decorator;
}

import pick from '@f/pick';
import mapObj from '@f/map-obj';
import t from 'tcomb';
import { vnode as element } from 'deku';

const { isThunk, isText, isEmpty }  = element;


export function normalize(comp) {
  const component = t.Function.is(comp) ? { render: comp } : comp;

  if(!t.Object.is(component) && !t.Function.is(component.render))
    throw new TypeError('component must be function or object with render function');

  if(!component.name && component.render.name !== '') component.name = component.render.name;
  return component;
}

export function mapAttributes(node, f, deep = false) {
  if(isText(node) || isEmpty(node)) return node;
  const name = isThunk(node) ? 'props' : 'attributes';
  const spec = {};

  spec[ name ] = mapObj(f, node[ name ]);
  if(deep) spec.children = node.children.map(child => mapAttributes(child, f, deep));

  return Object.assign({}, node, spec);
}

export const isHandler = name => (
  name.length > 2 &&
  name.slice(0, 2) === 'on' &&
  name[ 2 ] === name[ 2 ].toUpperCase()
);

const defaultOpts = {
  deep:     false,
  cache:    null,
  model:    (component, model) => model,
  render:   (component, model) => component.render(model),
  onCreate: (component, model) => component.onCreate ? component.onCreate(model) : null,
  onUpdate: (component, model) => component.onUpdate ? component.onUpdate(model) : null,
  onRemove: (component, model) => component.onRemove ? component.onRemove(model) : null
};

function applySpec(spec, rawComponent) {
  const cacheEntry = spec.cache ? spec.cache.get(rawComponent) : null;

  if(cacheEntry) return cacheEntry;
  const component = normalize(rawComponent);
  const hooks = pick(['onCreate', 'onUpdate', 'onRemove'], spec);
  const componentSpec = mapObj(hook => model => hook(component, spec.model(component, model)), hooks);

  componentSpec.render = function(model) {
    const decoratedModel = spec.model(component, model);
    const node = spec.render(component, decoratedModel);

    return spec.deep ? decorateNode(spec, node) : node;
  };

  const decorated =  Object.assign({}, component, componentSpec);

  if(spec.cache) spec.cache.set(rawComponent, decorated);
  return decorated;
}

function decorateNode(spec, node) {
  const nodeSpec = {};

  if(isThunk(node)) nodeSpec.component = applySpec(spec, node.component);
  if(!t.Nil.is(node.children)) nodeSpec.children = node.children.map(child => decorateNode(spec, child));

  return Object.assign({}, node, nodeSpec);
}

export function specDecorator(options) {
  const cache = options.deep && !options.cache ? new WeakMap() : options.cache;
  const spec = Object.assign({}, defaultOpts, options, { cache });

  return function(component) {
    const decorated = applySpec(spec, component);

    return decorated;
  };
}

import { element } from 'deku';

import decorators from './decorators';
import scopes from './scopes';
import views from './views';

import cycle from './utils/cycle';

/**
 * @see http://dekujs.github.io/deku/docs/basics/components.html#model
 * @typedef {Object} model
 * @property {Map<string, any>} model.props
 * @property {string} model.path - unique path to the component
 * @property {Context} model.context
 * @property {Array<vnode>} model.children
 */

/**
 * @see http://dekujs.github.io/deku/docs/basics/components.html
 * @typedef {Object} Component
 * @property {function(model: model): vnode} Component.render
 * @property {function(model: model)} Component.onCreate
 * @property {function(model: model)} Component.onUpdate
 * @property {function(model: model)} Component.onRemove
 */

/**
 * @see http://dekujs.github.io/deku/docs/basics/elements.html
 * @typedef {Object} vnode
 * @property {string|Component} vnode.type
 * @property {Map<string, any>} vnode.attributes
 * @property {Array<vnode>} vnode.children
 */

const helper = {
  cycle
};


/**
 * jsx compatible element function.
 * @external {h} http://dekujs.github.io/deku/docs/api/element.html
 */

const h = element;

export {
  scopes,
  views,
