function assert(T, ctx, name = 'undefinedh') {
  const raw = ctx.get(true);
  let error = null;

  try {
    T(raw);
  } catch(e) {
    e.message = (`
      component decorator mount error!
      component.name: ${name}
      \n`).concat(e.message);

    error = e;
  }
  if(error) throw error;
}

function decorateHook(component, T, name) {
  if(!component[ name ]) return null;

  return function(model) {
    assert(T, model.context, component.Name);
    return component[ name ](model);
  };
}

export default T => rawComponent => {
  const component = normalize(rawComponent);
  const decorator = curry(decorateHook)(component, T);
  const spec = generateSpec(decorator);

  return Object.assign({}, component, spec);
};

import normalize from './../utils/normalizeComponent';

/**
 * decorate component
 * @param  {Array} decoration - list of component decorators
 * @param {Component} component -  the target
 * @return {Component} decorated component
 */

const f = decoration => rawComponent => {
  const component = normalize(rawComponent);

  return decoration
   .slice()
   .reverse()
   .reduce((target, decorator) => decorator(target), component);
};

export default f;

import JSONPointer from './../JSONPointer';
import normalize from './../utils/normalizeComponent';

const controllerDecorator = controller => rawComponent => {
  const component = normalize(rawComponent);

  function render(model) {
    const dispatch = function(name) {
      if(!controller[ name ]) return () => null;
      return function(e) {
        const decoratedDispatch = function(patchSet = []) {
          model.dispatch(patchSet.map(function(patch) {
            if(!patch.path) return patch;
            const newPatch = Object.assign({}, patch);
            const pointer = JSONPointer.ofString(patch.path);

            newPatch.path = model.context.transform.apply(pointer).toRFC();
            return newPatch;
          }));
        };

        const patchSet = controller[ name ](e, model, decoratedDispatch);

        decoratedDispatch(patchSet);
      };
    };

    const decoratedModel = Object.assign({}, model, { dispatch });

    return component.render(decoratedModel);
  }

  return Object.assign({}, component, { render });
};

export default controllerDecorator;

