import t from 'tcomb';

/**
 * tcomb type
 * @external {Type} https://github.com/gcanti/tcomb/blob/master/docs/API.md
 */

const Type = t.irreducible('Type', t.isType);

/**
 * 'booleanized' check function from tcomb
 * @type Function
 * @param  {Type} T - tcomb type
 * @param  {Any} x - value to check
 * @return {Boolean}
 */

const check = t.func([Type, t.Any], t.Bool).of(function(T, x) {
  let result = null;

  /* eslint space-after-keywords: 0 */
  try {
    T(x);
    result = true;
  } catch(e) {
    result = false;
  }
  return result;
});

export default check;

/**
 * creates a choke that throttle fuction calls
 *
 * The returned choke function will throttle calls with the same path argument
 * for delta milliseconds.
 *
 * @param  {Number} delta - the delta between calls [ms]
 * @return {function(path: String, f: Function)}
 */

export default function choke(delta) {
  const cache = {};

  function call(path, f) {
    const lastTime = cache[ path ];
    const currentTime = Date.now();

    if(lastTime && currentTime - lastTime < delta) return;

    cache[ path ] = Date.now();
    f();
  }

  return call;
}

