/**
 * This function is used to create the render part of your app.
 * The returned Rx.Subject listen to state events.
 * State is used as context when rendering component with deku inside container.
 * All actions dispatched inside component, will be emitted from subject.
 * @param  {DOMElement} container - container dom element
 * @param  {Component} component - deku component
 * @return {Subject}
 */

export default curry(function createRenderSubject(container, component, input) {
  const dispatchStream = flyd.stream();
  const dispatch = val => dispatchStream(val);
  const render = createDekuApp(container, dispatch);

  flyd.on(function(ctx) {
    const el = element(component, { __ctx: ctx.get(true) });

    render(el, ctx);
  }, input);

  return dispatchStream;
});

export default function(f, arity) {
  const length = Number.isInteger(arity) ? arity : f.length;

  function call(...args) {
    if(args.length >= length) return f.apply(this, args);
    return (...newArgs) => call(...args.concat(newArgs));
  }

  return call;
}

import flyd from 'flyd';

/**
 * pipes a to b and b to a -> cycle a and b
 * @param  {Stream} a - flyd stream
 * @param  {Stream} b - flyd stream
 * @return {Stream} empty stream, use to end
 */

export default function cycle(a, b) {
  const start = flyd.stream();
  const streamA = a(start);
  const streamB = b(streamA);

  // flyd.on(val => console.log('a emitted:', val), streamA);
  // flyd.on(val => console.log('b emitted:', val.get(true)), streamB);

  return flyd.on(x => start(x), streamB);
}

/**
 * decorate component
 * @param  {Array} decoration - list of component decorators
 * @param {Component} component -  the target
 * @return {Component} decorated component
 */

export default function decorate(decoration, component) {
  console.log(decoration);
  return decoration
    .slice()
    .reverse()
    .reduce((target, decorator) => decorator(target), component);
}

/**
 * deep freeze of Arrays and Objects
 * @param  {object} val - will be deeply freezed
 * @return {object} freezed val
 */

export default function deepFreeze(val) {
  // Reminder: typeof [1,2] -> 'object'
  if(typeof val !== 'object') return val;

  const propNames = Object.getOwnPropertyNames(val);

  propNames.forEach(name => deepFreeze(val[ name ]));

  return Object.freeze(val);
}

import t from 'tcomb';

/**
 * generate update spec for component hooks
 * @param  {function} decorator - decorator is applied on all targets
 * @param  {Array} targets - targets for decorator
 * @return {Component}
 */

export default function generateSpec(decorator, targets = ['onCreate', 'onUpdate', 'render', 'onRemove']) {
  const spec = {};

  targets.forEach(function(target) {
    const result = decorator(target);

    if(t.Nil.is(result)) return;

    spec[ target ] = result;
  });

  return spec;
}

import t from 'tcomb';

export default function normalize(component) {
  if(t.Function.is(component)) return { render: component };

  if(t.Object.is(component) && t.Function.is(component.render))
    return component;

  throw new TypeError('component must be function or object with render function');
}

import t from 'tcomb';

export function patch(...args) {
  if(args.length % 2 !== 0) throw new Error('length of arguments must be even');

  const pairs = args.reduce(function(current, e, i) {
    if(i % 2 === 0) current.push([]);
    current[ current.length - 1 ].push(e);

    return current;
  }, []);

  const patchSet = pairs.map(pair => ({
    op:    'replace',
    path:  pair[ 0 ],
    value: pair[ 1 ]
  }));

  return patchSet;
}

const toPatch = (path, value) => ({
  op: 'replace',
  path,
  value
});

export default function handler(...args) {
  if(args.length % 2 !== 0) throw new Error('length of arguments must be even');

  const pairs = args.reduce(function(current, e, i) {
    if(i % 2 === 0) current.push([]);
    current[ current.length - 1 ].push(e);

    return current;
  }, []);

  return (_, { context }) => pairs.map(function([path, value]) {
    if(!t.Function.is(value)) return toPatch(path, value);

    const currentValue = context.get(path, true);
    const nextValue = value(currentValue);

    return toPatch(path, nextValue);
  });
}

